import React from 'react';
import { Link, useNavigate } from 'react-router-dom';
import * as Auth from '../utils/Auth';

function Login() {
    const [formValue, setFormValue] = useState({
        username: '',
        password: ''
      })

      const handleChange = (e) => {
        const {name, value} = e.target;

        setFormValue({
          ...formValue,
          [name]: value
        });
      }

      const navigate = useNavigate();

      const handleSubmit = (e) => {
        e.preventDefault();
        if (!formValue.username || !formValue.password){
          return;
        }
        auth.authorize(formValue.username, formValue.password)
          .then((data) => {
          if (data.jwt){
            setFormValue({username: '', password: ''});
            handleLogin();
            navigate('/diary', {replace: true});
          }
          })
          .catch(err => console.log(err));
      }

    return(
        <div className="login">
            <div className="login__container">
                <p className="login__title">Вход</p>
                <form className="login__form" name="login__form">
                    <label className="login__form-field">
                    <input
                        className="login__input login__input-email"
                        type="email"
                        name="email"
                        id="emailAddress"
                        placeholder="Email"
                        defaultValue=""
                        required=""
                        minLength={4}
                        maxLength={30}
                        // onChange={handleChangeName}
                    />
                    <span className="login__error-visible email-error-visible" />
                    </label>
                    <label className="login__form-field">
                        <input
                            className="login__input login__input-password"
                            type="password"
                            name="password"
                            id="userPassword"
                            placeholder="Пароль"
                            defaultValue=""
                            required=""
                            minLength={2}
                            maxLength={200}
                            // onChange={handleChangeDescription}
                        />
                        <span className="login__error-visible password-error-visible" />
                    </label>
                    <button
                        className="login__button-submit"
                        type="submit"
                        name="button-submit"
                        >
                        Войти
                    </button>
                </form>
            </div>
        </div>
    );
}

export default Login
