import React, { useState } from "react";
import { Link, useNavigate } from 'react-router-dom';
import * as Auth from '../utils/Auth';
import InfoTooltip from './InfoTooltip';
import done from "../images/done.svg";

const Register = ({openInfoToolTip}) => {
  const [formValue, setFormValue] = useState({email: '', password: ''})
  const navigate = useNavigate();

  const handleChange = (e) => {
    const {name, value} = e.target;

    setFormValue({
      ...formValue,
      [name]: value
    });
  }

  const handleSubmit = (e) => {
     e.preventDefault();
     const { email, password } = formValue;
     Auth.register(email, password)
     .then((res) => {
        console.log(res);
        openInfoToolTip();
        navigate('/sign-in', {replace: true});
        console.log('hello');
        })
    }

    return(
        <div className="register">
        <div className="register__container">
            <p className="register__title">Регистрация</p>
            <form className="register__form" name="register__form" onSubmit={handleSubmit}>
                <label className="register__form-field">
                <input
                    className="register__input register__input-email"
                    type="email"
                    name="email"
                    id="emailAddress"
                    placeholder="Email"
                    value={formValue.email}
                    required=""
                    minLength={4}
                    maxLength={30}
                    onChange={handleChange}
                />
                <span className="register__error-visible email-error-visible" />
                </label>
                <label className="register__form-field">
                    <input
                        className="register__input register__input-password"
                        type="password"
                        name="password"
                        id="userPassword"
                        placeholder="Пароль"
                        value={formValue.password}
                        required=""
                        minLength={2}
                        maxLength={200}
                        onChange={handleChange}
                    />
                    <span className="register__error-visible password-error-visible" />
                </label>
                <button
                    className="register__button-submit"
                    type="submit"
                    name="button-submit"
                    onSubmit={handleSubmit}
                    >
                    Зарегистрироваться
                </button>
                <p className="register__question">Уже зарегистрированы? <Link className="register__login-link" to='/sign-in' >Войти</Link></p>
            </form>
            <InfoTooltip
                isOpen={isInfoTooltipPopup}
                onClose={closeInfoToolTip}
                >
                <div className={`popup__info-tooltip-form`}>
                    <img
                        className="popup__tooltip-image"
                        src={done}
                        alt="Выполнено"
                    />
                    <p className="popup__text-info-tooltip">Вы успешно зарегистрировались!</p>
                </div>
            </InfoTooltip>
        </div>
    </div>
    );
}

export default Register
